import { Button } from "@mui/material";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import React, { useEffect, useState } from "react";
import Modal from "react-modal";
import { Link, useNavigate } from "react-router-dom";
import "../Css/Home.css";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

Modal.setAppElement("body");

const HomeGreige = () => {
  const [modalIsOpen, setIsOpen] = useState(false);
  const [msg, setMsg] = useState("updating");

  const navigate = useNavigate();
  useEffect(() => {
    document.title = "QMS";
    if (localStorage.getItem("token") == null) navigate("/");
  }, []);

  function closeModal() {
    setIsOpen(false);
  }

  const import_rolls = async () => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem("server-ip")}/inspaction/update_data`,
        {
          method: "GET",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
        }
      );
      const re = await response.text();
      if (re === "Complete") {
        setMsg("successful");
      } else {
        setMsg("failed");
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="head-title">Home</div>
        {localStorage
          .getItem("permissions")
          .includes("import_batch_and_roll_from_T-Aps") ? (
          <div style={{ marginLeft: 10 }}>
            <Button
              variant="outlined"
              onClick={() => {
                import_rolls();
                setIsOpen(true);
                setMsg("updating");
              }}
              style={{ padding: "0px", margin: "0px" }}
            >
              <img src={require("../database.gif")} height={35} />
            </Button>
          </div>
        ) : (
          <div></div>
        )}

        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <Modal
        isOpen={modalIsOpen}
        // onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Reprocess"
        shouldCloseOnOverlayClick={false}
      >
        {msg === "successful" ? (
          <div>
            <div style={{ width: "100%" }}>
              <img src={require("../complete.gif")} height={250} />
            </div>
            <div style={{ display: "flex", justifyContent: "end" }}>
              <Button variant="outlined" onClick={closeModal}>
                OK
              </Button>
            </div>
          </div>
        ) : msg === "failed" ? (
          <h1>Failed!!!</h1>
        ) : (
          <div>
            <div
              style={{
                width: "100%",
                display: "flex",
                justifyContent: "center",
              }}
            >
              <img src={require("../loading.gif")} height={250} />
            </div>
            <span>This might take 1.5-2 minutes. Please be patient.</span>
          </div>
        )}
      </Modal>
      <Link to="/add_greige_gsm" className="card">
        <div className="card-content">Add GSM</div>
      </Link>
      {/* <Link to="/add_shade" className="card">
        <div className="card-content">Add Shade</div>
      </Link> */}
      {/* <Link to="/add_high_defects" className="card">
        <div className="card-content">Add No Of High Defects</div>
      </Link> */}
      {/* <Link to="/pass_fail_greige_status" className="card">
        <div className="card-content">Pass Fail Status</div>
      </Link> */}
      <Link to="/batch_status_greige" className="card">
        <div className="card-content">Batch Status</div>
      </Link>
      <Link to="/reports" className="card">
        <div className="card-content">Reports</div>
      </Link>
    </div>
  );
};

export default HomeGreige;
