import {
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@mui/material";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import HomeIcon from "@mui/icons-material/Home";
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Css/AddUser.css";

const AddMachines = () => {
  const [mNumber, setMNumber] = useState();
  const navigate = useNavigate();

  useEffect(() => {
    if (localStorage.getItem("token") == null) navigate("/");
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      console.log("In");
      const response = await fetch(
        `http://${localStorage.getItem(
          "server-ip"
        )}:3000/inspaction/add_machine`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            mNumber,
          }),
        }
      );
      const json = await response.text();
      console.log(json);
      if (json === "success") {
        window.location.reload();
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="home-button">
          <Button
            variant="outlined"
            startIcon={<HomeIcon />}
            onClick={() => {
              navigate("/home");
            }}
          >
            Home
          </Button>
        </div>
        <div className="head-title">Add User</div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <div>
        <div className="table-section">
          <form onSubmit={handleSubmit} className="form-section">
            <div className="input-element">
              <div className="input-element-title">Machine Number:</div>
              <div>
                <TextField
                  variant="outlined"
                  margin="normal"
                  required
                  name="id"
                  label="Enter Machine No..."
                  id="id"
                  onChange={(event) => setMNumber(event.target.value)}
                />
              </div>
            </div>
            <div style={{ marginLeft: "30%" }}>
              <Button
                type="submit"
                variant="outlined"
                startIcon={<LogoutTwoToneIcon />}
              >
                Add
              </Button>
            </div>
          </form>
          <div
            className="info-table"
            style={{ height: "400px", overflow: "auto" }}
          >
            <TableContainer component={Paper}>
              <Table sx={{ minWidth: 350 }} aria-label="simple table">
                <TableHead>
                  <TableRow>
                    <TableCell>Roll Number</TableCell>
                    <TableCell>Roll GSM</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {rolls.map((row) =>
                    !row.GSM ? (
                      <div key={row.id}></div>
                    ) : (
                      <TableRow
                        key={row.id}
                        sx={{
                          "&:last-child td, &:last-child th": { border: 0 },
                        }}
                      >
                        <TableCell component="th" scope="row">
                          {row.roll_number}
                        </TableCell>
                        <TableCell align="center">{row.GSM}</TableCell>
                      </TableRow>
                    )
                  )}
                </TableBody>
              </Table>
            </TableContainer>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddMachines;
