import { Button, TextField } from "@mui/material";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import HomeIcon from "@mui/icons-material/Home";
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Css/AddUser.css";

const AddMachines = () => {
  const [eId, setEID] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (localStorage.getItem("token") == null) navigate("/");
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch(
        "http://10.12.3.182:3000/inspaction/add_user",
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            eId,
            password,
          }),
        }
      );
      const json = await response.json();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="home-button">
          <Button
            variant="outlined"
            startIcon={<HomeIcon />}
            onClick={() => {
              navigate("/home");
            }}
          >
            Home
          </Button>
        </div>
        <div className="head-title">Add User</div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <div>
        <form onSubmit={handleSubmit} className="form-section">
          <div className="input-element">
            <div className="input-element-title">Machine Number:</div>
            <div>
              <TextField
                variant="outlined"
                margin="normal"
                required
                name="id"
                label="Enter Machine No..."
                id="id"
                onChange={(event) => setEID(event.target.value)}
              />
            </div>
          </div>
          <div style={{ marginLeft: "30%" }}>
            <Button
              type="submit"
              variant="outlined"
              startIcon={<LogoutTwoToneIcon />}
            >
              Add
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddMachines;
