import React, { useContext, useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Button from "@mui/material/Button";
import {
  Alert,
  AlertTitle,
  Input,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@mui/material";
import SearchableDropdown from "./SearchableDropdown";
import "../Css/AddGSM.css";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import HomeIcon from "@mui/icons-material/Home";

export default function EditInspection() {
  const location = useLocation();
  const { state } = location;
  const [inspectionData, setInspectionData] = React.useState([]);
  const [inspectionDataBefore, setInspectionDataBefore] = React.useState([]);
  const [batch, setBatch] = useState();
  const [gsmData, setGsmData] = useState({});

  const getRolls = async () => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/get_inspection_data`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: state.batch,
            roll: state.roll,
          }),
        }
      );

      if (!response.ok) {
        throw new Error(`Request failed with status: ${response.status}`);
      }

      const json = await response.json();
      if (json.length > 0) {
        console.log(json);
        setInspectionDataBefore(json);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const saveGSM = async () => {
    if (batch == "Select batch...") {
      setMsg("Batch Number");
    } else {
      try {
        const url = `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/save_gsm`;
        const options = {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: batch,
            gsmData,
          }),
        };

        const response = await fetch(url, options);
        const json = await response.text();
        if (json == "done") {
          window.location.reload();
        }
      } catch (error) {
        console.error(`Error: ${error}`);
      }
    }
  };

  const navigate = useNavigate();
  useEffect(() => {
    getRolls();
    if (localStorage.getItem("token") == null) navigate("/");
  }, []);

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="home-button">
          <Button
            variant="outlined"
            startIcon={<HomeIcon />}
            onClick={() => {
              navigate("/home");
            }}
          >
            Home
          </Button>
        </div>
        <div className="head-title">Edit Inspection</div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <div>
        <div className="table-section-add-machines">
          <div
            className="info-table-status"
            style={{ height: "500px", overflow: "auto" }}
          >
            <TableContainer component={Paper}>
              <Table sx={{ minWidth: 350 }} aria-label="simple table">
                <TableHead>
                  <TableRow style={{ backgroundColor: "#D2EBFE" }}>
                    <TableCell align="center">Defect</TableCell>
                    <TableCell align="center">One Point</TableCell>
                    <TableCell align="center">Two Point</TableCell>
                    <TableCell align="center">Three Point</TableCell>
                    <TableCell align="center">Four Point</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {inspectionDataBefore.map((row) => (
                    <TableRow
                      key={row.DefectName}
                      sx={{
                        "&:last-child td, &:last-child th": { border: 0 },
                      }}
                    >
                      <TableCell
                        align="center"
                        style={{ backgroundColor: "#D2EBFE " }}
                      >
                        {row.DefectName}
                      </TableCell>
                      <TableCell align="center">
                        <span style={{ color: "blue" }}>{row.OnePoint}</span>
                        <span> --to-- </span>
                      </TableCell>
                      <TableCell align="center">
                        <Input
                          style={{ width: "35px" }}
                          value={row.TwoPoint}
                        ></Input>
                      </TableCell>
                      <TableCell align="center">
                        <Input
                          style={{ width: "35px" }}
                          value={row.ThreePoint}
                        ></Input>
                      </TableCell>
                      <TableCell align="center">
                        <Input
                          style={{ width: "35px" }}
                          value={row.FourPoint}
                        ></Input>
                      </TableCell>

                      {/* <TableCell>
                        <div>
                          <Button
                            id="demo-positioned-button"
                            aria-controls={
                              open ? "demo-positioned-menu" : undefined
                            }
                            aria-haspopup="true"
                            aria-expanded={open ? "true" : undefined}
                            onClick={handleClick}
                          >
                            EDIT
                          </Button>
                          <Menu
                            id="demo-positioned-menu"
                            aria-labelledby="demo-positioned-button"
                            anchorEl={anchorEl}
                            open={open}
                            onClose={handleClose}
                            anchorOrigin={{
                              vertical: "top",
                              horizontal: "left",
                            }}
                            transformOrigin={{
                              vertical: "top",
                              horizontal: "left",
                            }}
                          >
                            <MenuItem
                              onClick={() => handleClose("/edit_inspection")}
                            >
                              Inspection
                            </MenuItem>
                            <MenuItem
                              onClick={() => handleClose("/edit_inspection")}
                            >
                              GSM
                            </MenuItem>
                            <MenuItem
                              onClick={() => handleClose("/edit_inspection")}
                            >
                              Shade
                            </MenuItem>
                          </Menu>
                        </div>
                      </TableCell> */}
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </div>
        </div>
      </div>
    </div>
  );
}
