import {
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@mui/material";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import React, { useEffect, useState } from "react";
import HomeIcon from "@mui/icons-material/Home";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import { Link, useNavigate } from "react-router-dom";
import "../Css/BatchStatus.css";
import SearchableDropdown from "./SearchableDropdown";

const BatchStatus = () => {
  const [bNumber, setBNumber] = useState("");
  const [batches, setBatches] = useState([]);
  const [status, setStatus] = useState([]);
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };
  const navigate = useNavigate();

  useEffect(() => {
    if (localStorage.getItem("token") == null) navigate("/");
  }, []);

  const getBatchs = async (item) => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/get_saved_batches`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: item,
          }),
        }
      );
      const json = await response.json();
      const arr = [];
      json.forEach((element) => {
        arr.push(element);
      });
      setBatches(arr);
    } catch (error) {
      console.error(error);
    }
  };

  const getBatchStatus = async () => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/get_batch_status`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber,
          }),
        }
      );

      if (!response.ok) {
        throw new Error(`Request failed with status: ${response.status}`);
      }

      const json = await response.json();
      if (json.length > 0) {
        setStatus(json);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch(
        `http://${localStorage.getItem("server-ip")}/inspaction/add_user`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            eId,
            password,
            userName,
          }),
        }
      );
      const json = await response.text();
      console.log(json);
      if (json === "success") {
        window.location.reload();
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem("server-ip")}/inspaction/delete_user`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            id,
          }),
        }
      );
      const json = await response.text();
      console.log(json);
      if (json === "success") {
        window.location.reload();
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="home-button">
          <Button
            variant="outlined"
            startIcon={<HomeIcon />}
            onClick={() => {
              navigate("/home");
            }}
          >
            Home
          </Button>
        </div>
        <div className="head-title">Add User</div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <div>
        <div className="batch-number-status">
          <div className="batch-number-dropdown-passfail">
            <div className="dropdown-legend">Batch Number</div>
            <div className="dropdown-passfail">
              <SearchableDropdown
                options={batches}
                label="WorkOrderId"
                id="id"
                selectedVal={bNumber}
                onSearch={getBatchs}
                handleChange={(val) => {
                  setBNumber(val);
                }}
              />
            </div>
          </div>
          <div className="search-roll-button">
            <Button variant="contained" onClick={() => getBatchStatus()}>
              Search
            </Button>
          </div>
        </div>
        <div className="table-section-add-machines">
          <div
            className="info-table-status"
            style={{ height: "400px", overflow: "auto" }}
          >
            <TableContainer component={Paper}>
              <Table sx={{ minWidth: 250 }} aria-label="simple table">
                <TableHead>
                  <TableRow style={{ backgroundColor: "#D2EBFE" }}>
                    <TableCell align="center">Roll Number</TableCell>
                    <TableCell align="center">Inspection Status</TableCell>
                    <TableCell align="center">GSM Status</TableCell>
                    <TableCell align="center">Shade Status</TableCell>
                    <TableCell align="center">High Defects Status</TableCell>
                    <TableCell align="center">Pass Fail Status</TableCell>
                    <TableCell align="center">Action</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {status.map((row) => (
                    <TableRow
                      key={row.Id}
                      sx={{
                        "&:last-child td, &:last-child th": { border: 0 },
                      }}
                    >
                      <TableCell
                        align="center"
                        style={{ backgroundColor: "#D2EBFE " }}
                      >
                        {row.roll_number}
                      </TableCell>
                      <TableCell
                        align="center"
                        style={{
                          backgroundColor:
                            row.InspectionStatus === "Done" ? "#34A119" : "red",
                          color: "white",
                        }}
                      >
                        {row.InspectionStatus}
                      </TableCell>
                      <TableCell
                        align="center"
                        style={{
                          backgroundColor:
                            row.GsmStatus === "Done" ? "#34A119" : "red",
                          color: "white",
                        }}
                      >
                        {row.GsmStatus}
                      </TableCell>
                      <TableCell
                        align="center"
                        style={{
                          backgroundColor:
                            row.ShadeStatus === "Done" ? "#34A119" : "red",
                          color: "white",
                        }}
                      >
                        {row.ShadeStatus}
                      </TableCell>
                      <TableCell
                        align="center"
                        style={{
                          backgroundColor:
                            row.NoOfHighDefectsStatus === "Done"
                              ? "#34A119"
                              : "red",
                          color: "white",
                        }}
                      >
                        {row.NoOfHighDefectsStatus}
                      </TableCell>
                      <TableCell
                        align="center"
                        style={{
                          backgroundColor:
                            row.PassFailStatus === "Done" ? "#34A119" : "red",
                          color: "white",
                        }}
                      >
                        {row.PassFailStatus}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BatchStatus;
