import { Button } from "@mui/material";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import React, { useEffect, useState } from "react";
import Modal from "react-modal";
import { Link, useNavigate } from "react-router-dom";
import "../Css/Home.css";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

Modal.setAppElement("body");

const ModuleSelect = () => {
  const navigate = useNavigate();
  useEffect(() => {
    document.title = "QMS";
    if (localStorage.getItem("token") == null) navigate("/");
  }, []);

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="head-title">Module Select</div>
        {/* {localStorage
          .getItem("permissions")
          .includes("import_batch_and_roll_from_T-Aps") ? (
          <div style={{ marginLeft: 10 }}>
            <Button
              variant="outlined"
              onClick={() => {
                import_rolls();
                setIsOpen(true);
                setMsg("updating");
              }}
              style={{ padding: "0px", margin: "0px" }}
            >
              <img src={require("../database.gif")} height={35} />
            </Button>
          </div>
        ) : (
          <div></div>
        )} */}

        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      {localStorage.getItem("permissions").includes("fabric_") ? (
        <Link to="/final_inspection" className="card">
          <div className="card-content">Final Fabric Inspection</div>
        </Link>
      ) : (
        <Link to="/final_inspection" className="card">
          <div className="card-content">Final Fabric Inspection</div>
        </Link>
      )}

      <Link to="/add_shade" className="card">
        <div className="card-content">Greige Fabric Inspection</div>
      </Link>
    </div>
  );
};

export default ModuleSelect;
