import { Button } from "@mui/material";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Css/PassFailStatus.css";
import SearchableDropdown from "./SearchableDropdown";

const PassFailStatus = () => {
  const [batches, setBatches] = React.useState([]);
  const [batch, setBatch] = useState("Select a Batch...");
  const [data, setData] = useState([]);

  const navigate = useNavigate();
  useEffect(() => {
    if (localStorage.getItem("token") == null) navigate("/");
    getBatchs();
  }, []);

  const getBatchs = async () => {
    try {
      const response = await fetch(
        "http://10.12.3.182:3000/inspaction/get_saved_batches",
        {
          method: "GET",
        }
      );
      const json = await response.json();
      const arr = [];
      json.forEach((element) => {
        arr.push(element);
      });
      setBatches(arr);
    } catch (error) {
      console.error(error);
    }
  };

  const getData = async () => {
    try {
      const response = await fetch(
        "http://localhost:3000/inspaction/get_data_for_passfail",
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            batch,
          }),
        }
      );

      if (!response.ok) {
        throw new Error(`Request failed with status: ${response.status}`);
      }

      const json = await response.json();
      if (json.length > 0) {
        json.forEach((element) => {
          // if(element.roll_number)
        });
        console.log(json);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="head-title">Pass Fail Status</div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <div className="bottom-section-passfail">
        <div className="batch-number-passfail">
          <div className="batch-number-dropdown-passfail">
            <div className="dropdown-legend">Batch Number</div>
            <div className="dropdown-passfail">
              <SearchableDropdown
                options={batches}
                label="WorkOrderId"
                id="id"
                selectedVal={batch}
                handleChange={(val) => {
                  setBatch(val);
                }}
              />
            </div>
          </div>
          <div className="search-roll-button">
            <Button variant="contained" onClick={() => getData()}>
              Search
            </Button>
          </div>
        </div>
        <div className="report-section-passfail">
          <div className="report-head-section-passfail">
            <div className="report-head-left-section-pasfail">
              <div className="data-block-passfail">
                <div>Buyer: </div>
                <div>value</div>
              </div>
              <div className="data-block-passfail">
                <div>Fabrication: </div>
                <div>value</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PassFailStatus;
