import {
  Button,
  Input,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@mui/material";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import HomeIcon from "@mui/icons-material/Home";
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Css/PassFailStatus.css";
import SearchableDropdown from "./SearchableDropdown";

const PassFailStatus = () => {
  const [batches, setBatches] = React.useState([]);
  const [batch, setBatch] = useState();
  const [data, setData] = useState([]);
  const [shrinkage, setShrinkage] = useState(0);
  const [oComment, setOComment] = useState("");
  const [statusData, setStatusData] = useState({});
  const status = [
    { id: 1, st: "Pass" },
    { id: 2, st: "Fail" },
  ];

  const navigate = useNavigate();
  useEffect(() => {
    if (localStorage.getItem("token") == null) navigate("/");
  }, []);

  const getBatchs = async (item) => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/get_saved_batches`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: item,
          }),
        }
      );
      const json = await response.json();
      const arr = [];
      json.forEach((element) => {
        arr.push(element);
      });
      setBatches(arr);
    } catch (error) {
      console.error(error);
    }
  };

  const getData = async () => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/get_data_for_passfail`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            batch,
          }),
        }
      );

      if (!response.ok) {
        throw new Error(`Request failed with status: ${response.status}`);
      }

      const json = await response.json();
      if (json.length > 0) {
        setData(json);
        setShrinkage(json[0].shrinkage);
        setOComment(json[0].OverallComment);
        const updatedStatusData = { ...statusData }; // create a copy of statusData
        json.forEach((element) => {
          updatedStatusData[element.roll_number] = {
            grade: element.status,
            Comment: element.Comment,
          }; // add new key-value pair to copy
        });
        setStatusData(updatedStatusData); // update statusData with the copy
      }
    } catch (error) {
      console.error(error);
    }
  };

  const confirmStatus = async () => {
    if (batch == "Select batch...") {
      setMsg("Batch Number");
    } else {
      try {
        const url = `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/confirm_status`;
        const options = {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: batch,
            shrinkage,
            oComment,
            statusData,
          }),
        };

        const response = await fetch(url, options);
        const json = await response.text();
        if (json == "done") {
          window.location.reload();
        }
      } catch (error) {
        console.error(`Error: ${error}`);
      }
    }
  };

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="home-button">
          <Button
            variant="outlined"
            startIcon={<HomeIcon />}
            onClick={() => {
              navigate("/home");
            }}
          >
            Home
          </Button>
        </div>
        <div className="head-title">Pass Fail Status</div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <div className="bottom-section-passfail">
        <div className="batch-number-passfail">
          <div className="batch-number-dropdown-passfail">
            <div className="dropdown-legend font-sizeS">Batch Number</div>
            <div className="dropdown-passfail">
              <SearchableDropdown
                options={batches}
                label="WorkOrderId"
                id="id"
                selectedVal={batch}
                onSearch={getBatchs}
                handleChange={(val) => {
                  setBatch(val);
                }}
              />
            </div>
          </div>
          <div className="search-roll-button">
            <Button variant="contained" onClick={() => getData()}>
              Search
            </Button>
          </div>
        </div>
        {data.length < 1 ? (
          <div className="report-section-passfail">
            <div>Select and search a batch to see the report...</div>
          </div>
        ) : (
          <form onSubmit={() => confirmStatus()}>
            <div className="report-section-passfail">
              <div className="report-head-section-passfail">
                <div className="report-head-left-section-pasfail">
                  <div className="data-block-passfail">
                    <div className="data-block-title-passfail">Buyer: </div>
                    <div style={{ marginLeft: "1%" }}>
                      {data.length < 1 ? "" : data[0].customer}
                    </div>
                  </div>
                  <div className="data-block-passfail">
                    <div className="data-block-title-passfail">
                      Fabrication:{" "}
                    </div>
                    <div style={{ marginLeft: "5px" }}>
                      {data.length < 1 ? "" : data[0].fabricname}
                    </div>
                  </div>
                  <div style={{ display: "flex" }}>
                    <div
                      className="data-block-passfail"
                      style={{ marginRight: "1%" }}
                    >
                      <div className="data-block-title-passfail">
                        Finish GSM:
                      </div>
                      <div style={{ marginLeft: "5px" }}>
                        {data.length < 1 ? "" : data[0].FinishGSM}
                      </div>
                    </div>
                    <div
                      className="data-block-passfail"
                      style={{ marginRight: "1%" }}
                    >
                      <div className="data-block-title-passfail">
                        Finish Width:
                      </div>
                      <div style={{ marginLeft: "5px" }}>
                        {data.length < 1 ? "" : data[0].FinishWidth}
                      </div>
                    </div>
                    <div className="data-block-passfail">
                      <div className="data-block-title-passfail">WFX Po: </div>
                      <div style={{ marginLeft: "5px" }}>
                        {data.length < 1 ? "" : data[0].WFX_PO}
                      </div>
                    </div>
                  </div>
                </div>
                <div className="report-head-right-section-pasfail">
                  <div className="data-block-passfail">
                    <div className="data-block-title-passfail">Color: </div>
                    <div style={{ marginLeft: "5px" }}>
                      {data.length < 1 ? "" : data[0].shade}
                    </div>
                  </div>
                  <div style={{ display: "flex" }}>
                    <div className="data-block-passfail">
                      <div className="data-block-title-passfail">
                        Batch QTY:{" "}
                      </div>
                      <div style={{ marginLeft: "5px" }}>
                        {data.length < 1 ? "" : data[0].batch_qty}
                      </div>
                    </div>
                    <div
                      className="data-block-passfail"
                      style={{ backgroundColor: "white" }}
                    >
                      <div
                        className="data-block-title-passfail"
                        style={{ paddingTop: "1%", width: "175px" }}
                      >
                        Shrinkage:
                      </div>
                      <div>
                        <Input
                          style={{ marginLeft: "1%" }}
                          type="number"
                          required
                          value={shrinkage}
                          onChange={(event) => {
                            setShrinkage(event.target.value);
                          }}
                        ></Input>
                      </div>
                    </div>
                    <div className="data-block-passfail">
                      <div className="data-block-title-passfail">
                        Effective MTR:
                      </div>
                      <div style={{ marginLeft: "5px" }}>
                        {data.length < 1
                          ? ""
                          : (
                              data[data.length - 1].running_sum_AL -
                              (data[data.length - 1].running_sum_AL *
                                (shrinkage / 100) +
                                data[data.length - 1].running_sum_NHD)
                            ).toFixed(2)}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div
                className="report-body-section-passfail"
                style={{ height: "100%", overflow: "auto" }}
              >
                <TableContainer component={Paper}>
                  <Table sx={{ minWidth: 700 }} aria-label="simple table">
                    <TableHead>
                      <TableRow>
                        <TableCell align="center">Roll Number</TableCell>
                        <TableCell>Total Defects</TableCell>
                        <TableCell>Total Points</TableCell>
                        <TableCell>Actual Width(inch)</TableCell>
                        <TableCell>Actual GSM</TableCell>
                        <TableCell>Shade</TableCell>
                        <TableCell>Roll Weight (KG)</TableCell>
                        <TableCell>Actual Length (MTR)</TableCell>
                        <TableCell>Point Rate</TableCell>
                        <TableCell>No of High Defects</TableCell>
                        <TableCell>Effective MTR</TableCell>
                        <TableCell>Grade</TableCell>
                        <TableCell>Comments</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {data.map((row) => (
                        <TableRow key={row.roll_number} className="p">
                          <TableCell align="center">
                            {row.roll_number}
                          </TableCell>
                          <TableCell align="center">
                            {row.TotalDefects}
                          </TableCell>
                          <TableCell align="center">
                            {row.TotalPoints}
                          </TableCell>
                          <TableCell align="center">
                            {row.ActualRollWidth}
                          </TableCell>
                          <TableCell align="center">{row.GSM}</TableCell>
                          <TableCell align="center">{row.roll_shade}</TableCell>
                          <TableCell align="center">
                            {row.ActualRollWeight}
                          </TableCell>
                          <TableCell align="center">
                            {row.ActualLength}
                          </TableCell>
                          <TableCell align="center">{row.PointRate}</TableCell>
                          <TableCell align="center">
                            {row.NoOfHighDefects}
                          </TableCell>
                          <TableCell align="center">
                            {(
                              row.ActualLength -
                              (row.ActualLength * (shrinkage / 100) +
                                row.NoOfHighDefects)
                            ).toFixed(2)}
                          </TableCell>
                          <TableCell align="center">
                            <div>
                              <SearchableDropdown
                                options={status}
                                label="st"
                                id="id"
                                selectedVal={statusData[row.roll_number].grade}
                                handleChange={(val) => {
                                  setStatusData({
                                    ...statusData,
                                    [row.roll_number]: {
                                      ...statusData[row.roll_number],
                                      grade: val,
                                    },
                                  });
                                }}
                              />
                            </div>
                          </TableCell>
                          <TableCell align="center">
                            <Input
                              placeholder="comment..."
                              value={statusData[row.roll_number].Comment}
                              onChange={(event) => {
                                setStatusData({
                                  ...statusData,
                                  [row.roll_number]: {
                                    ...statusData[row.roll_number],
                                    Comment: event.target.value,
                                  },
                                });
                              }}
                            ></Input>
                          </TableCell>
                        </TableRow>
                      ))}
                      {data.length > 0 ? (
                        <TableRow>
                          <TableCell></TableCell>
                          <TableCell></TableCell>
                          <TableCell></TableCell>
                          <TableCell align="center">
                            {data[data.length - 1].running_sum_AWI}
                          </TableCell>
                          <TableCell></TableCell>
                          <TableCell></TableCell>
                          <TableCell align="center">
                            {data[data.length - 1].running_sum_AWE}
                          </TableCell>
                          <TableCell align="center">
                            {data[data.length - 1].running_sum_AL}
                          </TableCell>
                          <TableCell></TableCell>
                          <TableCell align="center">
                            {data[data.length - 1].running_sum_NHD}
                          </TableCell>
                          <TableCell align="center" colSpan={3}>
                            <div
                              style={{
                                backgroundColor: "white",
                                display: "flex",
                                alignItems: "center",
                              }}
                            >
                              <div
                                style={{
                                  paddingTop: "1%",
                                  fontSize: "15px",
                                  fontWeight: "bold",
                                }}
                              >
                                Overall Comment:{" "}
                              </div>
                              <div>
                                <Input
                                  style={{ marginLeft: "1%" }}
                                  placeholder="Comment..."
                                  value={oComment}
                                  onChange={(event) =>
                                    setOComment(event.target.value)
                                  }
                                ></Input>
                              </div>
                            </div>
                          </TableCell>
                        </TableRow>
                      ) : null}
                    </TableBody>
                  </Table>
                </TableContainer>
              </div>
              <div className="save-button">
                <button type="submit" className="buttonStyles">
                  Save
                </button>
              </div>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default PassFailStatus;
