import React, { useContext, useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Button from "@mui/material/Button";
import {
  Alert,
  AlertTitle,
  Input,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@mui/material";
import "../Css/AddGSM.css";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import HomeIcon from "@mui/icons-material/Home";

export default function ExcessSplit() {
  const location = useLocation();
  const { state } = location;
  //this is for initial data, which will not been changed after the edit by user.
  const [inspectionDataBefore, setInspectionDataBefore] = React.useState([]);
  //this is the updated data after the user edits inspection details. See the data structure and edit block to understand why two variables are declared.
  const [inspectionData, setInspectionData] = React.useState([]);
  //this is for Actual Width, Actual Weight and Cutable Width edited data is also stored in this as its data stucture is simple.
  const [inspectionWWWData, setInspectionWWWData] = React.useState([]);
  //this is for initial data, which will not been changed after the edit by user (for split roll).
  const [inspectionDataBeforeSplit, setInspectionDataBeforeSplit] =
    React.useState([]);
  //this is the updated data after the user edits inspection details. See the data structure and edit block to understand why two variables are declared (for split roll).
  const [inspectionDataSplit, setInspectionDataSplit] = React.useState([]);
  //this is for Actual Width, Actual Weight and Cutable Width edited data is also stored in this as its data stucture is simple (for split roll).
  const [inspectionWWWDataSplit, setInspectionWWWDataSplit] = React.useState(
    []
  );
  const [onePointCount, setOnePointCount] = React.useState({
    initial: 0,
    actual: 0,
    split: 0,
  });
  const [twoPointCount, setTwoPointCount] = React.useState({
    initial: 0,
    actual: 0,
    split: 0,
  });
  const [threePointCount, setThreePointCount] = React.useState({
    initial: 0,
    actual: 0,
    split: 0,
  });
  const [fourPointCount, setFourPointCount] = React.useState({
    initial: 0,
    actual: 0,
    split: 0,
  });
  const [batch, setBatch] = useState();
  const [gsmData, setGsmData] = useState({});

  const getInspectionData = async () => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/get_inspection_data`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: state.batch,
            roll: state.roll,
          }),
        }
      );

      if (!response.ok) {
        throw new Error(`Request failed with status: ${response.status}`);
      }

      const json = await response.json();
      if (json.length > 0) {
        let data = [];
        setInspectionDataBefore(json);
        let oneCount = 0;
        let twoCount = 0;
        let threeCount = 0;
        let fourCount = 0;
        json.map((item) => {
          if (item.OnePoint > 0) {
            oneCount += item.OnePoint;
          }
          data.push({
            ...item,
            OnePoint: 0,
            TwoPoint: 0,
            ThreePoint: 0,
            FourPoint: 0,
          });
        });
        setOnePointCount({
          ...onePointCount,
          initial: oneCount,
          actual: oneCount,
        });
        setInspectionDataBeforeSplit(data);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const updateInspection = async () => {
    if (batch == "Select batch...") {
      setMsg("Batch Number");
    } else {
      try {
        const url = `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/update_inspection`;
        const options = {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: state.batch,
            roll: state.roll,
            roll_id: state.roll_id,
            USERID: localStorage.getItem("token"),
            inspectionData: inspectionData,
          }),
        };

        const response = await fetch(url, options);
        const json = await response.text();
        if (json == "done") {
          window.location.reload();
        }
      } catch (error) {
        console.error(`Error: ${error}`);
      }
    }
  };

  const updateInspectionWWW = async () => {
    if (batch == "Select batch...") {
      setMsg("Batch Number");
    } else {
      try {
        const url = `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/update_inspection_www`;
        const options = {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: state.batch,
            roll_id: state.roll_id,
            USERID: localStorage.getItem("token"),
            inspectionWWWData: inspectionWWWData,
          }),
        };

        const response = await fetch(url, options);
        const json = await response.text();
        if (json == "done") {
          window.location.reload();
        }
      } catch (error) {
        console.error(`Error: ${error}`);
      }
    }
  };

  const navigate = useNavigate();
  useEffect(() => {
    getInspectionData();
    if (localStorage.getItem("token") == null) {
      navigate("/");
    } else {
      if (!localStorage.getItem("permissions").includes("edit_inspection")) {
        return navigate("/access-denied");
      }
    }
  }, []);

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="home-button">
          <Button
            variant="outlined"
            startIcon={<HomeIcon />}
            onClick={() => {
              navigate("/home");
            }}
          >
            Home
          </Button>
        </div>
        <div className="head-title">Edit Inspection</div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <div>
        <div className="table-section-edit-inspection">
          {/* Existing roll configaration */}
          <div className="inspection-table-section">
            <div style={{ width: "700px" }}>
              <TableContainer component={Paper}>
                <Table sx={{ minWidth: 450 }} aria-label="simple table">
                  <TableHead>
                    <TableRow style={{ backgroundColor: "#D2EBFE" }}>
                      <TableCell align="center">Defects</TableCell>
                      <TableCell align="center">One Point</TableCell>
                      <TableCell align="center">Two Point</TableCell>
                      <TableCell align="center">Three Point</TableCell>
                      <TableCell align="center">Four Point</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {inspectionDataBefore.map((row) => (
                      <TableRow
                        key={row.DefectName}
                        sx={{
                          "&:last-child td, &:last-child th": { border: 0 },
                        }}
                      >
                        <TableCell
                          align="center"
                          style={{ backgroundColor: "#D2EBFE " }}
                        >
                          {row.DefectName}
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>{row.OnePoint}</span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              setOnePointCount({
                                ...onePointCount,
                                actual:
                                  parseInt(onePointCount.actual) +
                                  (parseInt(event.target.value) - row.OnePoint),
                              });
                              setInspectionData({
                                ...inspectionData,
                                [row.DefectCode]: {
                                  ...inspectionData[row.DefectCode],
                                  OnePoint:
                                    event.target.value < 0
                                      ? null
                                      : event.target.value,
                                },
                              });
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>{row.TwoPoint}</span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              setInspectionData({
                                ...inspectionData,
                                [row.DefectCode]: {
                                  ...inspectionData[row.DefectCode],
                                  TwoPoint:
                                    event.target.value < 0
                                      ? null
                                      : event.target.value,
                                },
                              });
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>
                            {row.ThreePoint}
                          </span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              setInspectionData({
                                ...inspectionData,
                                [row.DefectCode]: {
                                  ...inspectionData[row.DefectCode],
                                  ThreePoint:
                                    event.target.value < 0
                                      ? null
                                      : event.target.value,
                                },
                              });
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>{row.FourPoint}</span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              setInspectionData({
                                ...inspectionData,
                                [row.DefectCode]: {
                                  ...inspectionData[row.DefectCode],
                                  FourPoint:
                                    event.target.value < 0
                                      ? null
                                      : event.target.value,
                                },
                              });
                            }}
                          ></Input>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
                <Button
                  onClick={() => {
