                          align="center"
                          style={{ backgroundColor: "#D2EBFE " }}
                        >
                          {row.DefectName}
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>{row.OnePoint}</span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              if (event.target.value.length < 1) {
                                setInspectionData(() => {
                                  // create copy of state object
                                  const copy = { ...inspectionData };

                                  // remove salary key from object
                                  delete copy[row.DefectCode];

                                  return copy;
                                });
                              } else {
                                setInspectionData({
                                  ...inspectionData,
                                  [row.DefectCode]: {
                                    ...inspectionData[row.DefectCode],
                                    OnePoint:
                                      event.target.value < 0
                                        ? null
                                        : parseInt(event.target.value),
                                  },
                                });
                              }
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>{row.TwoPoint}</span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              if (event.target.value.length < 1) {
                                setInspectionData(() => {
                                  // create copy of state object
                                  const copy = { ...inspectionData };

                                  // remove salary key from object
                                  delete copy[row.DefectCode];

                                  return copy;
                                });
                              } else {
                                setInspectionData({
                                  ...inspectionData,
                                  [row.DefectCode]: {
                                    ...inspectionData[row.DefectCode],
                                    TwoPoint:
                                      event.target.value < 0
                                        ? null
                                        : parseInt(event.target.value),
                                  },
                                });
                              }
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>
                            {row.ThreePoint}
                          </span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              if (event.target.value.length < 1) {
                                setInspectionData(() => {
                                  // create copy of state object
                                  const copy = { ...inspectionData };

                                  // remove salary key from object
                                  delete copy[row.DefectCode];

                                  return copy;
                                });
                              } else {
                                setInspectionData({
                                  ...inspectionData,
