                                  ...inspectionDataSplit,
                                  [row.DefectCode]: {
                                    ...inspectionDataSplit[row.DefectCode],
                                    OnePoint:
                                      event.target.value < 0
                                        ? null
                                        : parseInt(event.target.value),
                                  },
                                });
                              }
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>{row.TwoPoint}</span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              if (event.target.value.length < 1) {
                                setInspectionDataSplit(() => {
                                  // create copy of state object
                                  const copy = { ...inspectionDataSplit };

                                  // remove salary key from object
                                  delete copy[row.DefectCode];

                                  return copy;
                                });
                              } else {
                                setInspectionDataSplit({
                                  ...inspectionDataSplit,
                                  [row.DefectCode]: {
                                    ...inspectionDataSplit[row.DefectCode],
                                    TwoPoint:
                                      event.target.value < 0
                                        ? null
                                        : parseInt(event.target.value),
                                  },
                                });
                              }
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>
                            {row.ThreePoint}
                          </span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              if (event.target.value.length < 1) {
                                setInspectionDataSplit(() => {
                                  // create copy of state object
                                  const copy = { ...inspectionDataSplit };

                                  // remove salary key from object
                                  delete copy[row.DefectCode];

                                  return copy;
                                });
                              } else {
                                setInspectionDataSplit({
                                  ...inspectionDataSplit,
                                  [row.DefectCode]: {
                                    ...inspectionDataSplit[row.DefectCode],
                                    ThreePoint:
                                      event.target.value < 0
                                        ? null
                                        : parseInt(event.target.value),
                                  },
                                });
                              }
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>{row.FourPoint}</span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              if (event.target.value.length < 1) {
                                setInspectionDataSplit(() => {
                                  // create copy of state object
                                  const copy = { ...inspectionDataSplit };

                                  // remove salary key from object
                                  delete copy[row.DefectCode];

                                  return copy;
                                });
                              } else {
                                setInspectionDataSplit({
                                  ...inspectionDataSplit,
                                  [row.DefectCode]: {
                                    ...inspectionDataSplit[row.DefectCode],
                                    FourPoint:
                                      event.target.value < 0
                                        ? null
                                        : parseInt(event.target.value),
                                  },
                                });
                              }
                            }}
                          ></Input>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </div>
            <div style={{ marginLeft: "2%" }}>
              <TableContainer component={Paper}>
                <Table sx={{ minWidth: 150 }} aria-label="simple table">
                  <TableHead>
                    <TableRow style={{ backgroundColor: "#D2EBFE" }}>
                      <TableCell align="center">Actual Weight</TableCell>
                      <TableCell align="center">Actual Width</TableCell>
                      <TableCell align="center">Cutable width</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    <TableCell>
                      <span style={{ color: "blue" }}>
