                            ...inspectionWWWData,
                            CutableWidth: event.target.value,
                          });
                        }}
                      ></Input>
                    </TableCell>
                  </TableBody>
                </Table>
                <Button
                  onClick={() => {
                    if (Object.keys(inspectionWWWData).length > 0)
                      updateInspectionWWW();
                  }}
                >
                  Update
                </Button>
              </TableContainer>
            </div>
          </div>
        </div>
        <div>
          <div>
            <span>
              <div>Initial</div>
              <div>OnePoint: {onePointCount.initial}</div>
            </span>
            <span>
              <div>Split</div>
              <div>OnePoint: {onePointCount.split}</div>
            </span>
            <span>
              <div>Actual</div>
              <div>OnePoint: {onePointCount.actual}</div>
            </span>
          </div>
          <div>
            <span>
              <div>Initial</div>
              <div>TwoPoint: {twoPointCount.initial}</div>
            </span>
            <span>
              <div>Split</div>
              <div>TwoPoint: {twoPointCount.split}</div>
            </span>
            <span>
              <div>Actual</div>
              <div>TwoPoint: {twoPointCount.actual}</div>
            </span>
          </div>
          <div>
            <button onClick={() => handleCalculatePoints()}>press</button>
          </div>
        </div>
      </div>
    </div>
  );
}
