import { Button } from "@mui/material";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import React, { useEffect, useState } from "react";
import Modal from "react-modal";
import { Link, useNavigate } from "react-router-dom";
import "../Css/Home.css";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

Modal.setAppElement("body");

const Home = () => {
  const [modalIsOpen, setIsOpen] = useState(false);

  const navigate = useNavigate();
  useEffect(() => {
    document.title = "QMS";
    if (localStorage.getItem("token") == null) navigate("/");
  }, []);

  function closeModal() {
    setIsOpen(false);
  }

  const import_rolls = async () => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem("server-ip")}/inspaction/update_data`,
        {
          method: "GET",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
        }
      );
      const re = await response.text();
      console.log(re);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="head-title">Home</div>
        <div>
          <Button
            variant="contained"
            onClick={() => {
              import_rolls;
            }}
          >
            Import
          </Button>
        </div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <Modal
        isOpen={modalIsOpen}
        // onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Reprocess"
      >
        <h1>Updating...</h1>
      </Modal>
      <Link to="/add_gsm" target="_blank" className="card">
        <div className="card-content">Add GSM</div>
      </Link>
      <Link to="/add_shade" target="_blank" className="card">
        <div className="card-content">Add Shade</div>
      </Link>
      <Link to="/add_high_defects" target="_blank" className="card">
        <div className="card-content">Add No Of High Defects</div>
      </Link>
      <Link to="/pass_fail_status" target="_blank" className="card">
        <div className="card-content">Pass Fail Status</div>
      </Link>
      <Link to="/batch_status" target="_blank" className="card">
        <div className="card-content">Batch Status</div>
      </Link>
      <Link to="/configuration" target="_blank" className="card">
        <div className="card-content">Configuration</div>
      </Link>
      <Link to="/reports" target="_blank" className="card">
        <div className="card-content">Reports</div>
      </Link>
    </div>
  );
};

export default Home;
