import React, { useContext, useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Button from "@mui/material/Button";
import {
  Alert,
  AlertTitle,
  Input,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@mui/material";
import SearchableDropdown from "./SearchableDropdown";
import "../Css/AddShade.css";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";

export default function AddShade() {
  const location = useLocation();
  const batchNo = location.state?.batchNo || "Select batch...";
  const [batches, setBatches] = React.useState([]);
  const [rolls, setRolls] = React.useState([]);
  const [batch, setBatch] = useState(batchNo);
  const [shadeData, setShadeData] = useState({});
  const [unique, setUnique] = useState(0);
  const [message, setMessage] = useState([]);
  const [msg, setMsg] = useState("");
  const [shades, setShades] = React.useState([
    { id: 1, shade: "A" },
    { id: 2, shade: "B" },
    { id: 3, shade: "C" },
    { id: 4, shade: "D" },
    { id: 5, shade: "E" },
    { id: 6, shade: "F" },
  ]);

  const getBatchs = async () => {
    try {
      const response = await fetch(
        "http://10.12.3.182:3000/inspaction/get_saved_batches",
        {
          method: "GET",
        }
      );
      const json = await response.json();
      const arr = [];
      json.forEach((element) => {
        arr.push(element);
      });
      setBatches(arr);
    } catch (error) {
      console.error(error);
    }
  };

  const getRolls = async (selectedItem) => {
    try {
      const response = await fetch(
        "http://localhost:3000/inspaction/get_roll_by_batch_no_for_gsm",
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: batch,
          }),
        }
      );

      if (!response.ok) {
        throw new Error(`Request failed with status: ${response.status}`);
      }

      const json = await response.json();
      if (json.length > 0) {
        const arr = [];
        json.forEach((element) => {
          arr.push({ ...element, roll_number: element.roll_number.toString() });
        });
        setRolls(arr);
      }
    } catch (error) {
      console.error(error);
    }
  };

  // const getData = async (rno) => {
  //   try {
  //     const response = await fetch(
  //       "http://10.12.3.182:3000/inspaction/get_data_by_batch_roll_no_web",
  //       {
  //         method: "POST",
  //         headers: {
  //           Accept: "application/json",
  //           "Content-Type": "application/json",
  //         },
  //         body: JSON.stringify({
  //           bNumber: batch,
  //           roll_number: rno,
  //         }),
  //       }
  //     );
  //     const json = await response.json();
  //     setCustomer(json[0]["customer"]);
  //     setFabrication(json[0]["fabricname"]);
  //     setColor(json[0]["shade"]);
  //     setPo(json[0]["WFX_PO"]);
  //     setMachineNumber(json[0]["machine_number"]);
  //     setRollWeight(json[0]["ActualRollWeight"]);
  //     setRollWidth(json[0]["ActualRollWidth"]);
  //     setDate(json[0]["Date"]);
  //     setUserId(json[0]["user_id"]);
  //     setGSM(json[0]["GSM"]);
  //   } catch (error) {
  //     console.error(error);
  //   }
  // };

  const saveShade = async () => {
    if (batch == "Select batch...") {
      setMsg("Batch Number");
    } else {
      try {
        const url = "http://localhost:3000/inspaction/save_shade";
        const options = {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: batch,
            shadeData,
          }),
        };

        const response = await fetch(url, options);
        const json = await response.text();
        if (json == "done") {
          window.location.reload();
        }
      } catch (error) {
        console.error(`Error: ${error}`);
      }
    }
  };

  const navigate = useNavigate();
  useEffect(() => {
    if (localStorage.getItem("token") == null) navigate("/");
    getBatchs();
  }, []);
  return (
    <div className="card-container">
      <div className="top-section">
        <div className="head-title">Add Shade</div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <div className="main-section">
        <div className="batch-number">
          <div className="dropdown-legend">Batch Number</div>
          <div className="dropdown">
            <SearchableDropdown
              options={batches}
              label="WorkOrderId"
              id="id"
              selectedVal={batch}
              handleChange={(val) => {
                setBatch(val);
              }}
            />
          </div>
        </div>
        <div className="search-roll-button">
          <Button variant="contained" onClick={() => getRolls(batch)}>
            Search Roll
          </Button>
        </div>

        <div className="table-section">
          <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell className="table-head">
                    <span className="table-head-text-alt">Roll Number</span>
                  </TableCell>
                  <TableCell>
                    <span className="table-head-text">Roll Shade</span>
                  </TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {rolls.map((row) =>
                  row.shade ? (
                    <div key={row.id}></div>
                  ) : (
                    <TableRow
                      key={row.id}
                      sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                    >
                      <TableCell component="th" scope="row">
                        {row.roll_number}
                      </TableCell>
                      <TableCell align="right">
                        <div style={{ marginLeft: "40%" }}>
                          <SearchableDropdown
                            options={shades}
                            label="shade"
                            id="id"
                            selectedVal={
                              shadeData[row.roll_number]?.shade ??
                              "Select a shade..."
                            }
                            handleChange={(val) => {
                              setShadeData({
                                ...shadeData,
                                [row.roll_number]: {
                                  shade: val < 0 ? null : val,
                                },
                              });
                            }}
                          />
                        </div>
                      </TableCell>
                    </TableRow>
                  )
                )}
              </TableBody>
            </Table>
          </TableContainer>
          <div className="save-button">
            <Button variant="contained" onClick={() => saveShade()}>
              Save
            </Button>
          </div>
        </div>
        <div className={msg.length < 1 ? "error-disable" : "error"}>
          <Alert severity="error">
            <AlertTitle>Error</AlertTitle>
            This is an error alert â€” <strong>Please fill {msg}!</strong>
          </Alert>
        </div>
      </div>
    </div>
  );
}
