import React, { useContext, useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Button from "@mui/material/Button";
import {
  Alert,
  AlertTitle,
  Input,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@mui/material";
import SearchableDropdown from "./SearchableDropdown";
import "../Css/AddGSM.css";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import HomeIcon from "@mui/icons-material/Home";

export default function EditInspection() {
  const location = useLocation();
  const { state } = location;
  const [inspectionDataBefore, setInspectionDataBefore] = React.useState([]);
  const [inspectionData, setInspectionData] = React.useState([]);
  const [batch, setBatch] = useState();
  const [gsmData, setGsmData] = useState({});

  const getRolls = async () => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/get_inspection_data`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: state.batch,
            roll: state.roll,
          }),
        }
      );

      if (!response.ok) {
        throw new Error(`Request failed with status: ${response.status}`);
      }

      const json = await response.json();
      if (json.length > 0) {
        setInspectionDataBefore(json);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const updateInspection = async () => {
    if (batch == "Select batch...") {
      setMsg("Batch Number");
    } else {
      try {
        const url = `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/update_inspection`;
        const options = {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: state.batch,
            roll: state.roll,
            roll_id: state.roll_id,
            inspectionData: inspectionData,
          }),
        };

        const response = await fetch(url, options);
        const json = await response.text();
        if (json == "done") {
          window.location.reload();
        }
      } catch (error) {
        console.error(`Error: ${error}`);
      }
    }
  };

  const navigate = useNavigate();
  useEffect(() => {
    getRolls();
    if (localStorage.getItem("token") == null) navigate("/");
  }, []);

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="home-button">
          <Button
            variant="outlined"
            startIcon={<HomeIcon />}
            onClick={() => {
              navigate("/home");
            }}
          >
            Home
          </Button>
        </div>
        <div className="head-title">Edit Inspection</div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <div>
        <div className="table-section-add-machines">
          <div className="inspection-table-section">
            <div>
              <TableContainer component={Paper}>
                <Table sx={{ minWidth: 350 }} aria-label="simple table">
                  <TableHead>
                    <TableRow style={{ backgroundColor: "#D2EBFE" }}>
                      <TableCell align="center">Defect</TableCell>
                      <TableCell align="center">One Point</TableCell>
                      <TableCell align="center">Two Point</TableCell>
                      <TableCell align="center">Three Point</TableCell>
                      <TableCell align="center">Four Point</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {inspectionDataBefore.map((row) => (
                      <TableRow
                        key={row.DefectName}
                        sx={{
                          "&:last-child td, &:last-child th": { border: 0 },
                        }}
                      >
                        <TableCell
                          align="center"
                          style={{ backgroundColor: "#D2EBFE " }}
                        >
                          {row.DefectName}
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>{row.OnePoint}</span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              setInspectionData({
                                ...inspectionData,
                                [row.DefectCode]: {
                                  ...inspectionData[row.DefectCode],
                                  OnePoint:
                                    event.target.value < 0
                                      ? null
                                      : event.target.value,
                                },
                              });
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>{row.TwoPoint}</span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              setInspectionData({
                                ...inspectionData,
                                [row.DefectCode]: {
                                  ...inspectionData[row.DefectCode],
                                  TwoPoint:
                                    event.target.value < 0
                                      ? null
                                      : event.target.value,
                                },
                              });
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>
                            {row.ThreePoint}
                          </span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              setInspectionData({
                                ...inspectionData,
                                [row.DefectCode]: {
                                  ...inspectionData[row.DefectCode],
                                  ThreePoint:
                                    event.target.value < 0
                                      ? null
                                      : event.target.value,
                                },
                              });
                            }}
                          ></Input>
                        </TableCell>
                        <TableCell align="center">
                          <span style={{ color: "blue" }}>{row.FourPoint}</span>
                          <span> --to-- </span>
                          <Input
                            style={{ width: "35px" }}
                            type="tel"
                            onChange={(event) => {
                              setInspectionData({
                                ...inspectionData,
                                [row.DefectCode]: {
                                  ...inspectionData[row.DefectCode],
                                  FourPoint:
                                    event.target.value < 0
                                      ? null
                                      : event.target.value,
                                },
                              });
                            }}
                          ></Input>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
                <Button
                  onClick={() => {
                    if (Object.keys(inspectionData).length > 0)
                      updateInspection();
                  }}
                >
                  Update
                </Button>
              </TableContainer>
            </div>
            <div style={{ width: "200px" }}>
              <TableContainer component={Paper}>
                <Table sx={{ minWidth: 150 }} aria-label="simple table">
                  <TableHead>
                    <TableRow style={{ backgroundColor: "#D2EBFE" }}>
                      <TableCell align="center">Defect</TableCell>
                      <TableCell align="center">One Point</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    <TableCell>1</TableCell>
                    <TableCell>3</TableCell>
                  </TableBody>
                </Table>
                <Button
                  onClick={() => {
                    if (Object.keys(inspectionData).length > 0)
                      updateInspection();
                  }}
                >
                  Update
                </Button>
              </TableContainer>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
