import { Button } from '@mui/material';
import LogoutTwoToneIcon from '@mui/icons-material/LogoutTwoTone';
import React, { useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../Css/Home.css';

const Configuration = () => {
  const navigate = useNavigate();

  useEffect(() => {
    if(localStorage.getItem('token') == null) navigate('/')
  }, []);
  
  return (
    <div className="card-container">
      <div className='top-section'>
        <div className='head-title'>
            Configuration
        </div>
        <div className='logout'>
          <Button variant="outlined" startIcon={<LogoutTwoToneIcon />} onClick={() => {localStorage.removeItem("token"); navigate('/')}}>Logout</Button>
        </div>
      </div>
      {localStorage.getItem('permissions').includes('add_use') ?
      (<Link to='/add_user' className="card">
        <div className="card-content">Add User</div>
      </Link>) : 
      <div></div>}
      {localStorage.getItem('permissions').includes('edit_permission') ?
      (<Link to='/user_permission' className="card">
        <div className="card-content">User Permission</div>
      </Link>) : 
      <div></div>}
      <Link to='/something' className="card">
        <div className="card-content">Roll Status</div>
      </Link>
    </div>
  );
};

export default Configuration;
