import { Button } from "@mui/material";
import React from "react";
import HomeIcon from "@mui/icons-material/Home";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import { Link, useNavigate } from "react-router-dom";

const HeaderBar = ({ title, module }) => {
  const navigate = useNavigate();
  return (
    <div className="top-section">
      <div className="top-section-left">
        {title != "Module Select" ? (
          localStorage
            .getItem("permissions")
            .includes("import_batch_and_roll_from_T-Aps") ? (
            <div className="home-button">
              <Button
                variant="outlined"
                startIcon={<HomeIcon />}
                onClick={() => {
                  navigate("/home_greige");
                }}
              >
                Home
              </Button>
            </div>
          ) : (
            <div></div>
          )
        ) : (
          <div></div>
        )}

        <div className="head-title">{title}</div>
        {module == "fabric" ? (
          localStorage
            .getItem("permissions")
            .includes("import_batch_and_roll_from_T-Aps") ? (
            <div style={{ marginLeft: 10, justifyItems: "center" }}>
              <Button
                variant="outlined"
                onClick={() => {
                  import_rolls();
                  setIsOpen(true);
                  setMsg("updating");
                }}
                style={{ padding: "0px", margin: "0px" }}
              >
                <img src={require("../database.gif")} height={35} />
              </Button>
            </div>
          ) : (
            <div></div>
          )
        ) : (
          <div></div>
        )}
      </div>

      <div className="top-section-right">
        <div className="user-name">{localStorage.getItem("name")}</div>
        <Button
          variant="outlined"
          style={{
            minWidth: "none",
            fontSize: "70%", // Adjust the font size as needed
            padding: "5px 10%", // Adjust the padding as needed
          }}
          startIcon={<LogoutTwoToneIcon />}
          onClick={() => {
            localStorage.removeItem("token");
            navigate("/");
          }}
        >
          Logout
        </Button>
      </div>
    </div>
  );
};

export default HeaderBar;
