import React, { useContext, useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Button from "@mui/material/Button";
import {
  Alert,
  AlertTitle,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@mui/material";
import SearchableDropdown from "./SearchableDropdown";
import "../Css/AddGSM.css";
import LogoutTwoToneIcon from "@mui/icons-material/LogoutTwoTone";
import HomeIcon from "@mui/icons-material/Home";

export default function EditInspection() {
  const location = useLocation();
  const [batches, setBatches] = React.useState([]);
  const [rolls, setRolls] = React.useState([]);
  const [batch, setBatch] = useState();
  const [gsmData, setGsmData] = useState({});
  const [msg, setMsg] = useState("");

  const getBatchs = async (item) => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/get_saved_batches`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: item,
          }),
        }
      );
      const json = await response.json();
      const arr = [];
      json.forEach((element) => {
        arr.push(element);
      });
      setBatches(arr);
    } catch (error) {
      console.error(error);
    }
  };

  const getRolls = async (selectedItem) => {
    try {
      const response = await fetch(
        `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/get_roll_by_batch_no_for_gsm`,
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: batch,
          }),
        }
      );

      if (!response.ok) {
        throw new Error(`Request failed with status: ${response.status}`);
      }

      const json = await response.json();
      if (json.length > 0) {
        const arr = [];
        json.forEach((element) => {
          arr.push({ ...element, roll_number: element.roll_number.toString() });
        });
        setRolls(arr);
        console.log(arr);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const saveGSM = async () => {
    if (batch == "Select batch...") {
      setMsg("Batch Number");
    } else {
      try {
        const url = `http://${localStorage.getItem(
          "server-ip"
        )}/inspaction/save_gsm`;
        const options = {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            bNumber: batch,
            gsmData,
          }),
        };

        const response = await fetch(url, options);
        const json = await response.text();
        if (json == "done") {
          window.location.reload();
        }
      } catch (error) {
        console.error(`Error: ${error}`);
      }
    }
  };

  const navigate = useNavigate();
  useEffect(() => {
    if (localStorage.getItem("token") == null) navigate("/");
  }, []);

  return (
    <div className="card-container">
      <div className="top-section">
        <div className="home-button">
          <Button
            variant="outlined"
            startIcon={<HomeIcon />}
            onClick={() => {
              navigate("/home");
            }}
          >
            Home
          </Button>
        </div>
        <div className="head-title">Batch Status</div>
        <div className="logout">
          <Button
            variant="outlined"
            startIcon={<LogoutTwoToneIcon />}
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/");
            }}
          >
            Logout
          </Button>
        </div>
      </div>
      <div>
        <div className="batch-number-status">
          <div className="batch-number-dropdown-passfail">
            <div className="dropdown-legend">Batch Number</div>
            <div className="dropdown-passfail">
              <SearchableDropdown
                options={batches}
                label="WorkOrderId"
                id="id"
                selectedVal={bNumber}
                onSearch={getBatchs}
                handleChange={(val) => {
                  setBNumber(val);
                }}
              />
            </div>
          </div>
          <div className="search-roll-button">
            <Button variant="contained" onClick={() => getBatchStatus()}>
              Search
            </Button>
          </div>
        </div>
        <div className="table-section-add-machines">
          <div
            className="info-table-status"
            style={{ height: "400px", overflow: "auto" }}
          >
            <TableContainer component={Paper}>
              <Table sx={{ minWidth: 250 }} aria-label="simple table">
                <TableHead>
                  <TableRow style={{ backgroundColor: "#D2EBFE" }}>
                    <TableCell align="center">Roll Number</TableCell>
                    <TableCell align="center">Inspection Status</TableCell>
                    <TableCell align="center">GSM Status</TableCell>
                    <TableCell align="center">Shade Status</TableCell>
                    <TableCell align="center">High Defects Status</TableCell>
                    <TableCell align="center">Pass Fail Status</TableCell>
                    <TableCell align="center">Action</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {status.map((row) => (
                    <TableRow
                      key={row.Id}
                      sx={{
                        "&:last-child td, &:last-child th": { border: 0 },
                      }}
                    >
                      <TableCell
                        align="center"
                        style={{ backgroundColor: "#D2EBFE " }}
                      >
                        {row.roll_number}
                      </TableCell>
                      <TableCell
                        align="center"
                        style={{
                          backgroundColor:
                            row.InspectionStatus === "Done" ? "#34A119" : "red",
                          color: "white",
                        }}
                      >
                        {row.InspectionStatus}
                      </TableCell>
                      <TableCell
                        align="center"
                        style={{
                          backgroundColor:
                            row.GsmStatus === "Done" ? "#34A119" : "red",
                          color: "white",
                        }}
                      >
                        {row.GsmStatus}
                      </TableCell>
                      <TableCell
                        align="center"
                        style={{
                          backgroundColor:
                            row.ShadeStatus === "Done" ? "#34A119" : "red",
                          color: "white",
                        }}
                      >
                        {row.ShadeStatus}
                      </TableCell>
                      <TableCell
                        align="center"
                        style={{
                          backgroundColor:
                            row.NoOfHighDefectsStatus === "Done"
                              ? "#34A119"
                              : "red",
                          color: "white",
                        }}
                      >
                        {row.NoOfHighDefectsStatus}
                      </TableCell>
                      <TableCell
                        align="center"
                        style={{
                          backgroundColor:
                            row.PassFailStatus === "Done" ? "#34A119" : "red",
                          color: "white",
                        }}
                      >
                        {row.PassFailStatus}
                      </TableCell>
                      <TableCell>
                        <div>
                          <Button
                            id="demo-positioned-button"
                            aria-controls={
                              open ? "demo-positioned-menu" : undefined
                            }
                            aria-haspopup="true"
                            aria-expanded={open ? "true" : undefined}
                            onClick={handleClick}
                          >
                            EDIT
                          </Button>
                          <Menu
                            id="demo-positioned-menu"
                            aria-labelledby="demo-positioned-button"
                            anchorEl={anchorEl}
                            open={open}
                            onClose={handleClose}
                            anchorOrigin={{
                              vertical: "top",
                              horizontal: "left",
                            }}
                            transformOrigin={{
                              vertical: "top",
                              horizontal: "left",
                            }}
                          >
                            <MenuItem
                              onClick={() => handleClose("/edit_inspection")}
                            >
                              Inspection
                            </MenuItem>
                            <MenuItem
                              onClick={() => handleClose("/edit_inspection")}
                            >
                              GSM
                            </MenuItem>
                            <MenuItem
                              onClick={() => handleClose("/edit_inspection")}
                            >
                              Shade
                            </MenuItem>
                          </Menu>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </div>
        </div>
      </div>
    </div>
  );
}
